export function getRandomPersona(category?: string): Persona {
  let pool = personas;
  if (category && category !== 'all') {
    pool = personas.filter(p => p.category === category);
  }
  return pool[Math.floor(Math.random() * pool.length)];
}

export function getPersonasByCategory(category: string): Persona[] {
  return personas.filter(p => p.category === category);
}

export function getRarityColor(rarity: string): string {
  switch (rarity) {
    case 'common': return '#9ca3af';
    case 'rare': return '#3b82f6';
    case 'epic': return '#a855f7';
    case 'legendary': return '#f59e0b';
    default: return '#9ca3af';
  }
}
